#!/bin/bash# Colors for outputRED='\033[0;31m'GREEN='\033[0;32m'YELLOW='\033[1;33m'NC='\033[0m' # No Color# Function to print colored outputprint_message() {    echo -e "${2}${1}${NC}"}# Check if .env file existsif [ ! -f .env ]; then    print_message "вќЊ .env file not found! Creating from .env.example..." "$YELLOW"    cp .env.example .env    print_message "вњ… .env file created. Please configure it before running." "$GREEN"fi# Wait for databaseprint_message "вЏі Waiting for database..." "$YELLOW"python manage.py wait_for_db# Run migrationsprint_message "рџ”„ Running migrations..." "$YELLOW"python manage.py migrate# Collect static filesprint_message "рџ“¦ Collecting static files..." "$YELLOW"python manage.py collectstatic --noinput# Create superuser if not existsprint_message "рџ‘¤ Checking for superuser..." "$YELLOW"python manage.py shell -c "from django.contrib.auth import get_user_modelUser = get_user_model()if not User.objects.filter(is_superuser=True).exists():    User.objects.create_superuser('admin', 'admin@justhd.com', 'admin123')    print('вњ… Superuser created: admin / admin123')else:    print('вњ… Superuser already exists')"# Start serverprint_message "рџљЂ Starting Django development server..." "$GREEN"python manage.py runserver 0.0.0.0:8000